/* eslint-disable no-undef */
import './App.css';
import React, {useState } from 'react';
import SimpleBottomNavigation from './SimpleBottomNavigation';

function App() {
  const [responseFromContent, setResponseFromContent] = useState('');
    const [url, setUrl] = useState('');

    /**
     * Get current URL
     */
    useEffect(() => {
      const queryInfo = {active: true, lastFocusedWindow: true};

      chrome.tabs && chrome.tabs.query(queryInfo, tabs => {
          const url = tabs[0].url;
          setUrl(url);
      });
    }, []);

  const sendTestMessage = () => {
        const message = {
            from: React,
            message: "Hello from React",
        }

      const queryInfo = {
          active: true,
          currentWindow: true
      };

      /**
       * We can't use "chrome.runtime.sendMessage" for sending messages from React.
       * For sending messages from React we need to specify which tab to send it to.
       */
      chrome.tabs && chrome.tabs.query(queryInfo, tabs => {
          const currentTabId = tabs[0].id;
          /**
           * Sends a single message to the content script(s) in the specified tab,
           * with an optional callback to run when a response is sent back.
           *
           * The runtime.onMessage event is fired in each content script running
           * in the specified tab for the current extension.
           */

      chrome.tabs.sendMessage(
            currentTabId,
            message,
            (response) => {
                setResponseFromContent(response);
            });
      });
    };

  const headingContainer = (
    <div className="user-header">
      <div className="placeholderCircle" />
      <div className="headingContainerRight">
        <div className="placeholderCircle2" />
        <div className="placeholderCircle2" />
      </div>
    </div>
  )

  const headingTitle = () => {
    return (
      <div className="headingNavTitleContainer">
        <p className="headingNavTitle">Favorites</p>
        <p className="headingNavDescription">My Favorite Collection</p>
      </div>
    )
  }

  const mainContainer = () => {
    return (
      <div className="galleryContainer">
        <div className="addToFavorite">
          <p className="addToFavText" >Add to</p> 
          <p className="addToFavText" >Favorites</p> 
        </div>
        <div className="addToFavorite"/>
        <div className="addToFavorite"/>
      </div>
    )
  }

  return (
    <div className="App">
      <div className="ext-container">
        {headingContainer}
        {headingTitle()}
        <button onClick={sendTestMessage}>SEND MESSAGE</button>
        <p>Response from content:</p>
        <p>
          {url}
          {responseFromContent}
        </p>
        {/* {mainContainer()} */}

        <SimpleBottomNavigation/>
      </div>

    </div>
  )

}

export default App;
